import java.io.*;
import java.util.*;
import java.text.*;

public class Oyente{
   private String sexo;
   private float masa;
   private float altura;
      //constructor
   public Oyente(String sexo, float masa, float altura){
      this.sexo = sexo;
      this.masa = masa;
      this.altura = altura;
   }
   public double calcularIMC(){
      return (double) masa/(altura*altura);
   }
   public int masaIdeal(){
      if (sexo == "m"){
         return (int) (altura-1)*100;
      }
      else
         return (int) ((altura-1)*100)-10;
   }
   public String toString(){
      DecimalFormat dosDigitos = new DecimalFormat("0.00");
      return "Su IMC es: "+ dosDigitos.format(calcularIMC()) + " . Su masa ideal es: "+ masaIdeal();
   }  
}
   class Principal{
      public static void main(String arg[]){
      Scanner s = new Scanner(System.in);
      System.out.println("Dime tu masa en kg");
      float masa = s.nextFloat();
      System.out.println("Dime tu altura en m");
      float altura = s.nextFloat();
      System.out.println("Dime tu sexo (m para masculino, f para femenino)");
      String sexo = s.next();
      Oyente oyente1 = new Oyente(sexo, masa, altura);
      System.out.println(oyente1);
      }
}
